% magno_waveletfunction [WaPower, PLI, PLIdiff] = wavelet_app_matfiles(filemat, SampRate, f0start, f0end, fdelta, bslsegment, refelec)if nargin < 6     refelec = [];     PLIdiff = []; endfor fileindex = 1:size(filemat,1)        PLIsum = [];  PLIdiff = [];  outname  = [deblank(filemat(fileindex,:))];  a = load(deblank(filemat(fileindex,:)));  data = eval(['a.' char(fieldnames(a))]);   NPointsNew = size(data,2);     NTrials = size(data,3);     NPointsNew = size(data,2);     NTrials = size(data,3);       % compute wavelets and their parameters   wavelet = gener_wav(NPointsNew, fdelta, f0start, f0end);   disp('size of waveletMatrix')  disp(size(wavelet))  disp (' frequency step for delta_f0 = 1 is ')  disp(SampRate/NPointsNew)        % create 3d matrix objects for wavelet   % channels * time * frequencies       waveletMat3d = repmat(wavelet, [1 1 size(data,1)]);     waveletMat3d = permute(waveletMat3d, [3, 2, 1]);         % loop over trials        disp(['trial index of '])    disp(NTrials)        for trialindex = 1:NTrials;                  Data = data(:,:,trialindex);                fprintf('.')                 if trialindex/10 == round(trialindex/10), disp(trialindex), end            Data = bslcorr(Data, bslsegment);                size(Data);        % window data with cosine square window        window = cosinwin(20, size(Data,2), size(Data,1));                 Data = Data .* window;                 data_pad3d = repmat(Data, [1 1 size(wavelet,1)]);             % transform data  to the frequency domain        data_trans = fft(data_pad3d, NPointsNew, 2);        thetaMATLABretrans = [];         ProdMat= waveletMat3d .*(data_trans);        thetaMATLABretrans = ifft(ProdMat, NPointsNew, 2);                % standardize instantaneous phase        stdcomplexphasemat = thetaMATLABretrans ./ abs(thetaMATLABretrans);                % if a reference sensor is given, calculate inter-site PLI for that        % sensor        if ~isempty (refelec)            PLIdiffmat = stdcomplexphasemat- (repmat(stdcomplexphasemat(refelec,:,:), [size(data,1), 1, 1]));                   PLIdiffmat(refelec,:,:) = ones(size( PLIdiffmat(refelec,:,:)));             %standardize the difference            PLIdiffmat = PLIdiffmat./(abs(PLIdiffmat));         end                              if trialindex == 1            WaPowerSum = abs(thetaMATLABretrans).* 10;             PLIsum = stdcomplexphasemat;                 if ~isempty (refelec)            PLIdiffsum = PLIdiffmat;                 end        else        WaPowerSum = WaPowerSum + abs(thetaMATLABretrans).* 10;         PLIsum = PLIsum+ stdcomplexphasemat;           if ~isempty (refelec)               PLIdiffsum = PLIdiffsum+ PLIdiffmat;            end        end     end % loop over trials        %final calculations: avg (and abs for PLI)        WaPower = WaPowerSum./NTrials;        PLI = abs(PLIsum./NTrials);            if ~isempty (refelec)         PLIdiff = abs(PLIdiffsum./NTrials);        end                 eval(['save ' outname '.pow3.mat WaPower'])    % eval(['save ' outname '.pli3.mat PLI'])       if ~isempty (refelec),  eval(['save ' outname '.ispl3_' num2str(refelec) '.mat PLIdiff']), end              end % loop over files%       %       fclose('all') % % 