function [normmat] = bslcorr_z(a, bslindices)% z-transfomr of matrix a% a = inmat: observations by variables% % if size(a, 2) > 1 % if it is a matrix or row vector; 	    for SubInd = 1 : size(a, 1)	         normmat(SubInd,:) = (a(SubInd,:) - mean(a(SubInd,bslindices))) ./ std(a(SubInd,bslindices));    end    elseif size(a, 2) == 1 % if it is a column vector            normmat = (a-mean(a))./std(a); end