% concatenates 2-D or 3-D mats (chan x time points x trials) in the third% dimension (trials) function [outmat] = concat_matfiles(filemat, outname)outmat =[]; for fileindex = 1 : size(filemat, 1)		 strucmat = load(deblank(filemat(fileindex, :)), '-mat');          mattemp = eval(['strucmat.' char(fieldnames(strucmat))]);       outmat = cat(3, outmat, mattemp);  end   if ~isempty(outname)        save(outname, 'outmat', '-mat')    end    